import React, { useEffect, useState } from "react";
import "./App.css";

function App() {
	const [question, setQuestion] = useState([]);

	const fetchData = () => {
		fetch("https://opentdb.com/api.php?amount=1")
			.then((response) => response.json())
			.then((resData) => setQuestion(resData.data));
	};

	return (
		<div className="App">
			<p>{question[0]}</p>
			<button
				type="button"
				name="question"
				value="New Question"
				onClick={fetchData}
			/>
		</div>
	);
}

export default App;
